def mock_heartrate():
    ser = serial.Serial("COM4", 115200, timeout=1)
    i = 0
    count = 0
    ok = True
    now = time.perf_counter()
    while True:
        ser.write(heartrate[i].encode() + "\n".encode())
        data = heartrate[i]
        if ok and int(data) == 347:
            then = now
            now = time.perf_counter()
            count += 1
            print("time", now, then)
            print("heartbeat", count)
            print(60 / (now - then))
            ok = False
        elif int(data) < 347:
            ok = True
        time.sleep(0.02)
        i += 1
        if i == len(heartrate):
            i = 0


def record():
    data_ = []
    ser = serial.Serial("COM3", 115200, timeout=1)
    start = time.perf_counter()
    try:
        while time.perf_counter() - start < 30:
            response = ser.readline().decode("utf-8").strip()
            if response:
                data = response.split(",")[-1]
                data_.append(data)
        with open("data.txt", "wb") as f:
            pickle.dump(data_, f)
        print(data_)
    finally:
        ser.close()


def get_bpm():
    ser = serial.Serial("COM3", 115200, timeout=1)
    serW = serial.Serial("COM4", 115200, timeout=1)
    count = 0
    ok = True
    now = time.perf_counter()
    try:
        while True:
            response = ser.readline().decode("utf-8").strip()
            if response:
                data = response.split(",")[-1]
                serW.write(data.encode() + "\n".encode())
                time.sleep(0.02)
                if ok and int(data) == 347:
                    then = now
                    now = time.perf_counter()
                    count += 1
                    print("heartbeat", count)
                    print(60 /(now - then))
                    ok = False
                elif not ok and int(data) < 347:
                    ok = True
    finally:
        ser.close()


